mapper.select:
select a.bno, b.mno, title, writer, price, final_price, board_img, m.user_name
from(
	select bno from board where bno > 0 




	limit #{pageStart}, #{qty}
) a left join board b
on a.bno = b.bno
left join member m
on b.mno = m.mno;

select a.bno, b.mno, title, writer, price, final_price, board_img, m.user_name
from(
	select bno from board where bno > 0 

	limit #{pageStart}, #{qty}
) a left join board b
on a.bno = b.bno
left join member m
on b.mno = m.mno
where b.mno = #{mno}

mapper.update:
update board set
final_price = final_price + #{donation}
where bno = #{bno}

update board set
cheer = #{cheer} + 1
where bno = #{bno}
